import React, { useState } from 'react';
import axios from 'axios';

type Question = {
  id: string;
  question: string;
  options: string[];
};

function fetchQuiz<T>(topic: string): Promise<T[]> {
  return axios.post('http://localhost:5000/api/quiz', { topic }).then(res => res.data);
}

const App: React.FC = () => {
  const [topics] = useState(["React", "JavaScript"]);
  const [selectedTopic, setSelectedTopic] = useState("");
  const [quiz, setQuiz] = useState<Question[]>([]);
  const [current, setCurrent] = useState(0);
  const [score, setScore] = useState(0);
  const [started, setStarted] = useState(false);

  const startQuiz = async () => {
    const data = await fetchQuiz<Question>(selectedTopic);
    setQuiz(data);
    setStarted(true);
  };

  const handleAnswer = async (option: string) => {
    const q = quiz[current];
    const response = {
      questionId: q.id,
      selectedOption: option,
      timestamp: current * 25
    };

    try {
      const res = await axios.post('http://localhost:5000/api/response', response);
      if (res.data.correct) {
        setScore(prev => prev + 1);
      }
    } catch { }

    setCurrent(prev => prev + 1);
  };

  if (!started) {
    return (
      <div style={{ padding: 20 }}>
        <h2>Select a Topic</h2>
        <input
          list="topics"
          placeholder="Choose or type..."
          value={selectedTopic}
          onChange={(e) => setSelectedTopic(e.target.value)}
        />
        <datalist id="topics">
          {topics.map((t) => (
            <option key={t} value={t} />
          ))}
        </datalist>
        <br /><br />
        <button onClick={startQuiz} disabled={!selectedTopic}>
          Start Quiz
        </button>
      </div>
    );
  }

  if (current >= quiz.length) {
    return <h2>Quiz finished! Your score: {score}/{quiz.length}</h2>;
  }

  const q = quiz[current];

  return (
    <div style={{ padding: 20 }}>
      <h2>Question {current + 1}:</h2>
      <p>{q.question}</p>
      {q.options.map((opt) => (
        <button
          key={opt}
          onClick={() => handleAnswer(opt)}
          style={{ display: "block", margin: "10px 0", padding: "10px" }}
        >
          {opt}
        </button>
      ))}
      <p>Current Score: {score}</p>
    </div>
  );
};

export default App;
